    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}CRUD Symfony - Editar Produto{% endblock %}

{% block body %}
<div class=\"container\">
    <div class=\"row justify-content-center\">
        <div class=\"col-md-12\">
            <div class=\"card\">
                <div class=\"card-header bg-dark text-white\">
                    <div class=\"d-flex justify-content-between w-100\">
                        <h5>Editar (CRUD Symfony)</h5>
                        <a href=\"{{ url('produto_index') }}\" class=\"btn-info btn-sm\">
                            <i class=\"fa fa-arrow-left\"></i> Voltar
                        </a>
                    </div>
                </div>
                {% for message in app.flashes('notice') %}
                    <div class=\"alert alert-success\">
                        {{ message }}
                    </div>
                {% endfor %}
                <div class=\"card-body\">
                    {{ form_start(form) }}
                        <div class=\"form-group\">
                            {{ form_label(form.nome, \"Nome do Produto\") }}
                            {{ form_widget(form.nome, {'attr':{'class':'form-control'}})}}
                        </div>
                        <div class=\"form-group\">
                            {{ form_label(form.descricao, \"Descrição\") }}
                            {{ form_widget(form.descricao, {'attr':{'class':'form-control'}})}}
                        </div>
                        <div class=\"form-group\">
                            {{ form_label(form.preco_unitario, \"Preço Unitário (R\$)\") }}
                            {{ form_widget(form.preco_unitario, {'attr':{'class':'form-control'}})}}
                        </div>
                        <div class=\"form-group\">
                            <button class=\"btn btn-info mt-2 text-white\">Atualizar</button>
                        </div>
                    {{ form_end(form) }}
                </div>
            </div>
        </div>
    </div>
</div>
{% endblock %}", "produto/edit.html.twig", "C:\\wamp64\\www\\crudSymfony\\templates\\produto\\edit.html.twig");
